import React from 'react';
import { ImageBackground, Button, View, Text, StyleSheet, Image } from 'react-native';

import Images from '../../config/Images';
import base from '../../themes/BasicTheme';
import { globalSty } from '../style/globalStyle';

import '../../config/i18n';
import {useTranslation} from 'react-i18next';

const HomeScreen = ( {navigation} ) => {

  const {t} = useTranslation();

    return (
      <View style={globalSty.container}>
        <ImageBackground source={Images.NABBackgroundLogo} resizeMode="cover" style={globalSty.image}>
          <Image style={globalSty.logo}
            source={Images.logo}/>
          <Text style={globalSty.texth1}>{t('HomeTran.welcome')}</Text>
          <Text style={globalSty.texth2}>{t('HomeTran.homeIntro')}</Text>
            <Button
              title={t('HomeTran.homeBtn')}
                onPress={() => navigation.navigate("Location")} style={base.Button}
            />
        </ImageBackground>
      </View>
    )
};



export default HomeScreen;
