import React, {useState} from 'react';
import { ImageBackground, Button, View, Text} from 'react-native';
import CheckBox from "../../config/CheckBox";
import { useForm } from "react-hook-form";


import Images from '../../config/Images';
import base from '../../themes/BasicTheme';
import { globalSty } from '../style/globalStyle';

import '../../config/i18n';
import {useTranslation} from 'react-i18next';

const intialState = {
  LGBTQ:false,
  Veteran:false,
  Victim_DV:false,
  Childern:false,
  Pregnant:false,
  Dog:false
};

const QuestionScreen = ( {navigation} ) => {

  const {t} = useTranslation();
  const [state, setState]=useState(intialState);
  const [toggleButton,setToggleButton] = useState(false)


    return (
      <View style={globalSty.container}>
        <ImageBackground source={Images.NABBackgroundLogo} resizeMode="cover" style={globalSty.image}>
          <Text>{t('QuestionTran.text')}</Text>
          <View style={globalSty.CheckBoxForm}>
          <CheckBox
                onPress={(value) => setState({...state,LGBTQ:value})}
                title={t('QuestionTran.LGBTQ')}
                isChecked={state.LGBTQ}
              />
            </View>
            <View style={globalSty.CheckBoxForm}>
          <CheckBox
                onPress={(value) => setState({...state,Veteran:value})}
                title={t('QuestionTran.Veteran')}
                isChecked={state.Veteran}
              />
            </View>
            <View style={globalSty.CheckBoxForm}>
          <CheckBox
                onPress={(value) => setState({...state,Victim_DV:value})}
                title={t('QuestionTran.Victim_DV')}
                isChecked={state.Victim_DV}
              />
            </View>
            <View style={globalSty.CheckBoxForm}>
          <CheckBox
                onPress={(value) => setState({...state,Childern:value})}
                title={t('QuestionTran.Childern')}
                isChecked={state.Childern}
              />
            </View>
            <View style={globalSty.CheckBoxForm}>
          <CheckBox
                onPress={(value) => setState({...state,Pregnant:value})}
                title={t('QuestionTran.Pregnant')}
                isChecked={state.Pregnant}
              />
            </View>
            <View style={globalSty.CheckBoxForm}>
          <CheckBox
                onPress={(value) => setState({...state,Dog:value})}
                title={t('QuestionTran.Dog')}
                isChecked={state.Dog}
              />
            </View>
           

            <Button
              title={t('QuestionTran.contBtn')}
                onPress={() => navigation.navigate("QuestionsTwo")} style={base.Button}
            />
        </ImageBackground>
      </View>
    )
};



export default QuestionScreen;
