import { StyleSheet } from 'react-native';

export const globalSty = StyleSheet.create({
  container: {
    flex: 1,
  },
  image: {
    flex: 1,
    justifyContent: 'center',
  },
  logo: {
    width: 300,
    alignSelf: 'center',
    padding: 50,
    
  },
  space:{
    padding: 3,
    backgroundColor: 'black'
  },
  texth1: {
    color: '#54c6e0',
    fontSize: 60,
    lineHeight: 60,
    fontWeight: 'bold',
    textAlign: 'center',
    backgroundColor: 'transparent',
  },
  texth2: {
    color: 'white',
    fontSize: 40,
    lineHeight: 60,
    fontWeight: 'bold',
    textAlign: 'center',
    backgroundColor: 'transparent',
  },
  Button: {
    borderBottomWidth: 15,
    text: 'white',
    alignItems: 'center',
    justifyContent: 'center',
    padding: 75,
    paddingHorizontal: 32,
    borderRadius: 4,
    elevation: 3,
    backgroundColor: 'black',
  },
  Btxt: {
    fontSize: 82,
    lineHeight: 21,
    fontWeight: 'bold',
    letterSpacing: 0.25,
    color: 'white',
  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    backgroundColor: 'white'
  },
  DropdownMenu: {
    flex: 0.5,
  },

  CheckBoxForm:{
    top: 10,
    flexDirection: 'row',
    alignItems: 'center',
    paddingBottom: 20
  }
  
});
